class I18n {
  private locale: Locale = 'en' // English as default

  constructor() {
    const savedLocale = localStorage.getItem('revenify:locale') as Locale
    if (savedLocale && (savedLocale === 'pt-BR' || savedLocale === 'en' || savedLocale === 'es')) {
      this.locale = savedLocale
    } else {
      const browserLang = navigator.language
      if (browserLang.startsWith('pt')) {
        this.locale = 'pt-BR'
      } else if (browserLang.startsWith('es')) {
        this.locale = 'es'
      } else {
        this.locale = 'en' // Default to English
      }
    }
  }

  translate(key: string, params?: Record<string, any>): string {
    const translation = translations[key]
    if (!translation) {
      console.warn(`Translation key not found: ${key}`)
      return key
    }
    let result = translation[this.locale] || translation['en'] || key
    
    // Interpolate parameters
    if (params) {
      Object.keys(params).forEach(paramKey => {
        result = result.replace(`{${paramKey}}`, String(params[paramKey]))
      })
    }
    
    return result
  }

  setLocale(locale: Locale, saveToSupabase = true) {
    this.locale = locale
    localStorage.setItem('revenify:locale', locale)
    
    if (saveToSupabase) {
      this.saveToSupabase(locale).catch(err => {
        console.warn('Failed to save locale to Supabase:', err)
      })
    }
    
    window.dispatchEvent(new CustomEvent('localechange'))
  }

  async saveToSupabase(locale: Locale) {
    try {
      const { supabase } = await import('@/lib/supabase')
      const { data: { user } } = await supabase.auth.getUser()
      
      if (!user) return

      await supabase.auth.updateUser({
        data: { locale }
      })
    } catch (error) {
      console.error('Error saving locale to Supabase:', error)
      throw error
    }
  }

  async loadFromSupabase(): Promise<Locale | null> {
    try {
      const { supabase } = await import('@/lib/supabase')
      const { data: { user } } = await supabase.auth.getUser()
      
      if (!user) return null

      const userLocale = user.user_metadata?.locale as Locale
      if (userLocale && (userLocale === 'pt-BR' || userLocale === 'en' || userLocale === 'es')) {
        return userLocale
      }
      
      return null
    } catch (error) {
      console.error('Error loading locale from Supabase:', error)
      return null
    }
  }

  getLocale(): Locale {
    return this.locale
  }
}

export const i18n = new I18n()
